#!/usr/bin/env python3
# -*- coding: utf-8 -*-

import base64, struct
bmp_data = base64.b64decode('')

def bmp_info(data):
    st = struct.unpack('<ccIIIIIIHH', data[0:30])
    if st[0] + st[1] == b'BM':
        return {
            'width': st[6],
            'height': st[7],
            'color': st[9]
        }
    return None

if __name__ == '__main__':
    bi = bmp_info(bmp_data)
    assert bi['width'] == 28
    assert bi['height'] == 10
    assert bi['color'] == 16
    with open(r'D:\tmp\1.bmp', 'wb') as f:
        f.write(bmp_data)
    print('ok')

